import {createStore} from "vuex";
import getSetting from "@/api/setting";

const actions = {}

const mutations = {}

const state = {
    setting:{}
}

getSetting().then(res=>{
    state.setting = res.data
})

console.log(state)

const store = createStore({
    actions, mutations, state
})


export default store
